package org.example.hashMapsAndSets;


import java.util.HashMap;
import java.util.Map;

public class MaximumWindowSubstring {
    public static void main(String[] args) {
        String s = "";
        String t = "";
        System.out.println(getMinSbstring(s, t));
    }

    public static String getMinSbstring(String s, String t){
        if(s == null || t == null || s.isEmpty()){
            return "";
        }
        if(s.length() < t.length()){
            return "";
        }
        if(s.length()==t.length()){
            if(s.equals(t)){
                return s;
            }
        }

        int leftPointer = 0;
        int rightPointer = t.length();
        char[] charArray = t.toCharArray();
        String minString = "";
        while(rightPointer <= s.length()){
            String subString = s.substring(leftPointer,rightPointer);
            if(containChars(subString,charArray)){
                if(subString.length() == t.length()){
                    return subString;
                }
                if(minString.isEmpty() || minString.length()>subString.length()) {
                    minString = subString;
                }
                leftPointer++;
            }else{
                rightPointer++;
            }
        }
        return minString;
    }

    public static boolean containChars(String text, char[] charArray){
        Map<Character, Integer> charCount = new HashMap<>();
        for (char c : charArray) {
            charCount.put(c, charCount.getOrDefault(c, 0) + 1);
        }

        int remainingChars = charArray.length;

        // Iterate through s to check if all characters in t are covered
        for (char c : text.toCharArray()) {
            if (charCount.containsKey(c)) {
                int count = charCount.get(c);
                if (count > 0) {
                    charCount.put(c, count - 1);
                    remainingChars--;
                    if (remainingChars == 0) {
                        return true; // All characters found
                    }
                }
            }
        }

        return false;
    }
}
